import React, { Component } from "react";
import { useRouter } from "next/router";
import { Pie, Doughnut } from 'react-chartjs-2'
import Navbar from "../../../components/Navbar";
import PageLoading from "../../../components/PageLoading";
import api from "../../../api";
import { Button } from "semantic-ui-react";
export default class Home extends Component {
  static getInitialProps({ query }) {
    return { query };
  }




  state = {
    loading: true,
    error: null,
    summoner: null,
    summonerIcon: null,
    winRatio: null
  };
  componentDidMount() {
    this.fetchData();
    console.log(this.props.match);
  }

  fetchData = async (e) => {
    this.setState({ loading: true, error: null });

    try {
      const data = await api.summoner.general(
        this.props.query.region,
        this.props.query.summonerName
      );
      this.setState({ loading: false, summoner: data });
    } catch (err) {
      this.setState({ loading: false, error: err });
    }
  };

  render() {
    if (this.state.loading) {
      return <PageLoading />;
    }

    if (this.state.error) {
      return <h1>INTERNAL SERVER ERROR</h1>;
    }

    return (
      <div>
        <Navbar />
        <div className="contenedor">
          <div className="profileIcon">
            <div className="item">
            <span className="notify-badge">{this.state.summoner.level}</span>
            <img
              src={this.state.summoner.iconUrl}
              alt={this.state.summoner.name}
            />
            </div>
          </div>
          <div className="summonerBasicInfo">
            <h1>{this.state.summoner.name}</h1>
            <Button content="Actualizar">
            </Button>
          </div>
          <div>

            <Doughnut data={this.data} /> 

          </div>
          <div></div>
        </div>
      </div>
    );
  }
}
