import React, { Component } from "react";
import { useRouter } from "next/router";
import { Pie, Doughnut } from 'react-chartjs-2'
import Navbar from "../../../components/Navbar";
import PageLoading from "../../../components/PageLoading";
import api from "../../../api";
import Lee from '../../../images/lee.png'

import { Button } from "semantic-ui-react";
export default class Home extends Component {
  static getInitialProps({ query }) {
    return { query };
  }




  state = {
    loading: true,
    error: null,
    summoner: null,
    summonerIcon: null,
    winRatio: null
  };
  componentDidMount() {
    this.fetchData();
  }

  fetchData = async (e) => {
    this.setState({ loading: true, error: null });

    try {
      const data = await api.summoner.general(
        this.props.query.region,
        this.props.query.summonerName
      );
      let win_ratio = 0
      let lose_ratio = 0
      win_ratio = data.win_ratio
      lose_ratio = 100 - win_ratio

      console.log(win_ratio)
      console.log(lose_ratio)


      let winRate

      winRate = {
        datasets: [{
            data: [win_ratio, lose_ratio],
            backgroundColor: ["#4ac036", "#f45247",],
        }],

        options: {
          legend: { display: false },
          title: {
            display: true,
            text: 'Predicted world population (millions) in 2050'
          }
        },
      };

      this.setState({winRatio: winRate})
      this.setState({ loading: false, summoner: data });

    } catch (err) {
      this.setState({ loading: false, error: err });
    }

  };


  render() {

    let emblem = `../../../images/ranked-emblems/Emblem_${this.state.summoner.ranked_solo_fives.tier}.png`

    if (this.state.loading) {
      return <PageLoading />;
    }

    if (this.state.error) {
    return <h1>{this.state.error}}</h1>;
    }

    return (
      <div>
        <Navbar />
        <div className="contenedor">
          <div className="profileIcon">
            <div className="item">
            <span className="notify-badge">{this.state.summoner.level}</span>
            <img
              src={this.state.summoner.iconUrl}
              alt={this.state.summoner.name}
            />
            </div>
          </div>
          <div className="summonerBasicInfo">
            <h1>{this.state.summoner.name}</h1>
            <Button content="Actualizar">
            </Button>
          </div>
          <div>

            <Doughnut data={this.state.winRatio} /> 

          </div>
          <div classname="RankedInfo">
            <img src={emblem}></img>
          </div>
        </div>
      </div>
    );
  }
}
